import React, { useState } from 'react';
import * as P from './styles';

const Patient: React.FC = () => {

    const Name = 'Gustav'

    const[store, setStore] = useState(false);
    const[clock, setClock] = useState(false);
    const[certificate, setCertificate] = useState(false);
    const[wallet, setWallet] = useState(false);
    const[calendar, setCalendar] = useState(false);
    const[question, setQuestion] = useState(false);
    const[security, setSecurity] = useState(false);

    const showStore = () => {

        if(store === true){
          document.getElementById('store')!.style.display = 'none';
          setStore(false);
          document.getElementById('arrowStore')!.style.transform = "rotate(-0deg)"
          document.getElementById('headerStore')!.style.borderRadius = "10px 10px 10px 10px";
        }
        else if(store === false){
          document.getElementById('store')!.style.display = 'block';
          document.getElementById('arrowStore')!.style.transform = "rotate(-180deg)"
          document.getElementById('headerStore')!.style.borderRadius = "10px 10px 0 0";
          document.getElementById('clock')!.style.display = 'none';
          document.getElementById('certificate')!.style.display = 'none';
          document.getElementById('wallet')!.style.display = 'none';
          document.getElementById('calendar')!.style.display = 'none';
          document.getElementById('security')!.style.display = 'none';
          document.getElementById('question')!.style.display = 'none';
          document.getElementById('headerClock')!.style.borderRadius = "10px 10px 10px 10px";
          document.getElementById('headerCertificate')!.style.borderRadius = "10px 10px 10px 10px";
          document.getElementById('headerWallet')!.style.borderRadius = "10px 10px 10px 10px";
          document.getElementById('headerCalendar')!.style.borderRadius = "10px 10px 10px 10px";
          document.getElementById('headerSecurity')!.style.borderRadius = "10px 10px 10px 10px";
          document.getElementById('headerQuestion')!.style.borderRadius = "10px 10px 10px 10px";
          document.getElementById('arrowClock')!.style.transform = "rotate(-360deg)"
          document.getElementById('arrowCertificate')!.style.transform = "rotate(-360deg)"
          document.getElementById('arrowWallet')!.style.transform = "rotate(-360deg)"
          document.getElementById('arrowCalendar')!.style.transform = "rotate(-360deg)"
          document.getElementById('arrowSecurity')!.style.transform = "rotate(-360deg)"
          document.getElementById('arrowQuestion')!.style.transform = "rotate(-360deg)"
          setStore(true);
          setClock(false);
          setCertificate(false);
          setWallet(false);
          setCalendar(false);
          setSecurity(false);
          setQuestion(false);
        }

    }


    return (

        <>

            <P.GlobalContainer>
                <P.Title>Welcome, {Name}</P.Title>
                <P.UtilRowContainer>
                    <P.MedicationButton>Medication</P.MedicationButton>
                    <P.PersonButton>Person Data</P.PersonButton>
                    <P.RecordButton>Record</P.RecordButton>
                </P.UtilRowContainer>

                <P.MedicationContainer id='Medication'>
                    <p>Ibuprofeno</p>
                    <p>Nimesulida</p>
                    <p>Chá</p>
                </P.MedicationContainer>

                <P.PersonContainer id='Person'>
                    <p>Joséfino</p>
                    <p>18 y</p>
                </P.PersonContainer>

                <P.RecordContainer id='Record'>
                    <p>Dr.Pato</p>
                    <p>20/10/2000</p>
                </P.RecordContainer>
            </P.GlobalContainer>

        </>

    );
};

export default Patient;
